$.get("{xen:link "backgroundchangejsreplier"}", {
    'nid' : nid
}, function (newBg) {
    if(newBg==''){return;};
    document.getElementById("XenForo").style.background = newBg;
    document.getElementById("XenForo").style.overflowY ='scroll !important';
    document.getElementById("XenForo").style.backgroundSize = 'cover';
});

</script>
